import React from "react";

const UseStateObject = () => {
  const [person, setPetson] = React.useState({
    name: "peter",
    age: 24,
    message: "random message",
  });
  // console.log(person);
  return (
    <>
      <h3>{person.name}</h3>
      <h3>{person.age}</h3>
      <h3>{person.message}</h3>
    </>
  );
};

export default UseStateObject;
