import React, { useState } from "react";

const ErrorExample = () => {
  console.log(useState);
  let title = "random Title";
  const handleClick = (event) => {
    title = "Hello people";
    console.log(title);
  };

  return (
    <React.Fragment>
      <h2>{title}</h2>
      <button type="button" className="btn" onClick={handleClick}>
        change title
      </button>
    </React.Fragment>
  );
};

export default ErrorExample;
